import React, { useState, useEffect, useRef } from 'react';
import styled from 'styled-components';
import { io } from 'socket.io-client';
import { FaVideo, FaVideoSlash, FaMicrophone, FaMicrophoneSlash } from 'react-icons/fa';

const AppContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 100vh;
  background-color: #1a1a1a;
  color: white;
`;

const VideoContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: 20px;
  width: 100%;
  max-width: 1200px;
  padding: 20px;
`;

const Video = styled.video`
  width: 100%;
  max-width: 600px;
  border-radius: 10px;
  background-color: #2a2a2a;
`;

const Controls = styled.div`
  display: flex;
  gap: 20px;
  margin-top: 20px;
`;

const Button = styled.button`
  padding: 10px 20px;
  border-radius: 5px;
  border: none;
  background-color: #4a4a4a;
  color: white;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  &:hover {
    background-color: #5a5a5a;
  }
`;

const RoomInput = styled.div`
  margin-bottom: 20px;
  display: flex;
  gap: 10px;
`;

const Input = styled.input`
  padding: 10px;
  border-radius: 5px;
  border: none;
  background-color: #2a2a2a;
  color: white;
`;

const ErrorMessage = styled.div`
  color: #ff4444;
  background-color: rgba(255, 68, 68, 0.1);
  padding: 10px 20px;
  border-radius: 5px;
  margin-bottom: 20px;
  text-align: center;
  max-width: 600px;
`;

function App() {
  const [roomId, setRoomId] = useState('');
  const [localStream, setLocalStream] = useState(null);
  const [remoteStreams, setRemoteStreams] = useState({});
  const [isVideoOn, setIsVideoOn] = useState(true);
  const [isAudioOn, setIsAudioOn] = useState(true);
  const localVideoRef = useRef();
  const socketRef = useRef();
  const peerConnections = useRef({});
  const [error, setError] = useState(null);

  useEffect(() => {
    // Initialize socket connection
    socketRef.current = io('http://localhost:3005');

    socketRef.current.on('peers-list', ({ peers }) => {
      peers.forEach(peerId => {
        createPeerConnection(peerId);
      });
    });

    socketRef.current.on('signal', ({ from, data }) => {
      if (peerConnections.current[from]) {
        peerConnections.current[from].signal(data);
      }
    });

    socketRef.current.on('peer-disconnected', ({ peerId }) => {
      if (peerConnections.current[peerId]) {
        peerConnections.current[peerId].close();
        delete peerConnections.current[peerId];
        setRemoteStreams(prev => {
          const newStreams = { ...prev };
          delete newStreams[peerId];
          return newStreams;
        });
      }
    });

    return () => {
      socketRef.current.disconnect();
    };
  }, []);

  const createPeerConnection = (peerId) => {
    const peerConnection = new RTCPeerConnection({
      iceServers: [
        { urls: 'stun:stun.l.google.com:19302' }
      ]
    });

    peerConnection.onicecandidate = (event) => {
      if (event.candidate) {
        socketRef.current.emit('signal', {
          to: peerId,
          data: { candidate: event.candidate }
        });
      }
    };

    peerConnection.ontrack = (event) => {
      setRemoteStreams(prev => ({
        ...prev,
        [peerId]: event.streams[0]
      }));
    };

    if (localStream) {
      localStream.getTracks().forEach(track => {
        peerConnection.addTrack(track, localStream);
      });
    }

    peerConnections.current[peerId] = peerConnection;
  };

  const startLocalStream = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({
        video: {
          width: { ideal: 1280 },
          height: { ideal: 720 },
          facingMode: "user"
        },
        audio: true
      });
      setLocalStream(stream);
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }
      setError(null);
    } catch (error) {
      console.error('Error accessing media devices:', error);
      setError('Could not access camera or microphone. Please make sure you have granted the necessary permissions and that your camera/microphone is not being used by another application.');
    }
  };

  const joinRoom = () => {
    if (roomId) {
      socketRef.current.emit('join-room', roomId);
      startLocalStream();
    } else {
      setError('Please enter a room ID');
    }
  };

  const toggleVideo = () => {
    if (localStream) {
      const videoTrack = localStream.getVideoTracks()[0];
      videoTrack.enabled = !videoTrack.enabled;
      setIsVideoOn(!isVideoOn);
    }
  };

  const toggleAudio = () => {
    if (localStream) {
      const audioTrack = localStream.getAudioTracks()[0];
      audioTrack.enabled = !audioTrack.enabled;
      setIsAudioOn(!isAudioOn);
    }
  };

  return (
    <AppContainer>
      {error && (
        <ErrorMessage>
          {error}
        </ErrorMessage>
      )}
      <RoomInput>
        <Input
          type="text"
          placeholder="Enter Room ID"
          value={roomId}
          onChange={(e) => setRoomId(e.target.value)}
        />
        <Button onClick={joinRoom}>Join Room</Button>
      </RoomInput>

      <VideoContainer>
        <Video
          ref={localVideoRef}
          autoPlay
          playsInline
          muted
        />
        {Object.entries(remoteStreams).map(([peerId, stream]) => (
          <Video
            key={peerId}
            autoPlay
            playsInline
            ref={video => {
              if (video) video.srcObject = stream;
            }}
          />
        ))}
      </VideoContainer>

      <Controls>
        <Button onClick={toggleVideo}>
          {isVideoOn ? <FaVideo /> : <FaVideoSlash />}
          {isVideoOn ? 'Turn Off Video' : 'Turn On Video'}
        </Button>
        <Button onClick={toggleAudio}>
          {isAudioOn ? <FaMicrophone /> : <FaMicrophoneSlash />}
          {isAudioOn ? 'Mute' : 'Unmute'}
        </Button>
      </Controls>
    </AppContainer>
  );
}

export default App;
